package LoopWhile;

public class LoopWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("1 while ");	
		int x =0;
		while(x<7) {
		System.out.println(x);	
		x++;
		}
		
		System.out.println("2 do while ");	
		do {
			System.out.println("do");
			x++;
		}
		while(x<10);
		
		
		}

		}



