package switchLOOP;

public class LOOPSWITCH {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
      int x = 8;
      switch(x) {
      case 1:
    	  System.out.println(x);
    	  break;
    	  
      case 2:
    	  System.out.println(x);
    	  break;
    	  
      case 3:
    	  System.out.println(x);
    	  break;
      default:
    	  System.out.println(x);
      }
      }

	}


